from flask import Flask, requestfrom flask_restful import Resource, Apiapp = Flask(__name__)api = Api(app)class sumNumbers(Resource):    def get(self, first_number, second_number):        result = int(first_number)+int(second_number)        return {'result': str(result)}api.add_resource(sumNumbers, '/analytics/addNumbers/<first_number>/<second_number>')if __name__ =='__main__':	app.run(host='0.0.0.0',port=80,debug=True)